// src/components/Torneios.js
import React from 'react';
import { Link } from 'react-router-dom'; // Usando Link do React Router para navegação
import { FaInstagram, FaYoutube } from 'react-icons/fa'; // Ícones para Instagram e YouTube

const Torneios = ({ eventos }) => {
  // Limite de 3 torneios por vez
  const torneiosParaExibir = eventos.slice(0, 3); 

  return (
    <section className="torneios">
      <h2>Próximos Torneios</h2>
      <div className="event-list">
        {torneiosParaExibir.map((evento) => (
          <Link to={`/event/${evento.id}`} key={evento.id} className="event-card-link">
            <div className="event-card">
              <div className="event-card-image">
                <img src={evento.image} alt={evento.name} className="event-image" />
              </div>
              <div className="event-card-info">
                <h3>{evento.name}</h3>
                <p>{evento.date}</p>
                <p>{evento.location}</p>
                <div className="social-links">
                  <a href={evento.instagram} target="_blank" rel="noopener noreferrer">
                    <FaInstagram />
                  </a>
                  <a href={evento.youtube} target="_blank" rel="noopener noreferrer">
                    <FaYoutube />
                  </a>
                </div>
              </div>
            </div>
          </Link>
        ))}
      </div>
      <button className="load-more-btn">Carregar Mais</button> {/* Botão para carregar mais torneios */}
    </section>
  );
};

export default Torneios;
