// src/components/Torneios.js
import React from 'react';
import { Link } from 'react-router-dom';
import { FaInstagram, FaYoutube } from 'react-icons/fa'; // Importando os ícones do Font Awesome

const Torneios = ({ eventos }) => {
  return (
    <div className="event-list">
      {eventos && eventos.length > 0 ? (
        eventos.map((evento) => (
          <Link to={`/event/${evento.id}`} key={evento.id} className="event-card-link">
            <div className="event-card">
              <img src={evento.image} alt={evento.name} className="event-image" />
              <div className="event-info">
                <h3>{evento.name}</h3>
                <p className="event-date">{evento.date}</p>
                <p className="event-location">{evento.location}</p>
                <div className="social-links">
                  <a href={`https://instagram.com/${evento.id}`} target="_blank" rel="noopener noreferrer">
                    <FaInstagram />
                  </a>
                  <a href={`https://youtube.com/${evento.id}`} target="_blank" rel="noopener noreferrer">
                    <FaYoutube />
                  </a>
                </div>
              </div>
            </div>
          </Link>
        ))
      ) : (
        <p>Não há torneios disponíveis no momento.</p>
      )}
    </div>
  );
};

export default Torneios;
